import Image from 'next/image';

interface OptimizedImageProps {
  src: string;
  alt: string;
  width?: number;
  height?: number;
  priority?: boolean;
  className?: string;
  quality?: number;
  fill?: boolean;
}

export default function OptimizedImage({
  src,
  alt,
  width = 400,
  height = 300,
  priority = false,
  className,
  quality = 90,
  fill = false,
}: OptimizedImageProps) {
  const blurDataUrl =
    'data:image/jpeg;base64,';

  if (fill || (width === 0 && height === 0)) {
    return (
      <Image
        src={src}
        alt={alt}
        fill
        priority={priority}
        quality={quality}
        className={className}
        placeholder='blur'
        blurDataURL={blurDataUrl}
        sizes='(max-width: 480px) 100vw, (max-width: 768px) 90vw, (max-width: 1200px) 70vw, 1450px'
        style={{
          objectFit: 'cover',
          objectPosition: 'center',
        }}
      />
    );
  }

  return (
    <Image
      src={src}
      alt={alt}
      width={width}
      height={height}
      priority={priority}
      quality={quality}
      className={className}
      placeholder='blur'
      blurDataURL={blurDataUrl}
      sizes='(max-width: 480px) 100vw, (max-width: 768px) 90vw, (max-width: 1200px) 70vw, 1450px'
    />
  );
}
